$( document ).ready(function() {
  configReviewItems(5);
  $("#Button_1").click(function(){ goToAndPlay(100); setReviewItem(1); $(this).find("img").attr("src", "course/images/1B.png"); document.getElementById("audio_01").pause();});
  $("#Button_2").click(function(){ goToAndPlay(200); setReviewItem(2); $(this).find("img").attr("src", "course/images/2B.png"); document.getElementById("audio_01").pause();});
  $("#Button_3").click(function(){ goToAndPlay(300); setReviewItem(3); $(this).find("img").attr("src", "course/images/3B.png"); document.getElementById("audio_01").pause();});
  $("#Button_4").click(function(){ goToAndPlay(400); setReviewItem(4); $(this).find("img").attr("src", "course/images/4B.png"); document.getElementById("audio_01").pause();});
  $("#Button_5").click(function(){ goToAndPlay(500); setReviewItem(5); $(this).find("img").attr("src", "course/images/5B.png"); document.getElementById("audio_01").pause();});

  $(".popupButtonClose").click(function(){ $( this ).parent().hide(); goToAndStop(99); document.getElementById("audio_01").pause();});
});

var DDCounter = 0;
var DDTotalTargets = 5;

document.addEventListener("dragstart", function(event) {
  event.dataTransfer.setData("Text", event.target.id);
  event.target.style.opacity = "0.9";
});

document.addEventListener("drag", function(event) {
});

document.addEventListener("dragend", function(event) {
  event.target.style.opacity = "1";
});

document.addEventListener("dragenter", function(event) {
event.preventDefault();
event.stopPropagation();
console.log("dragenter");
if(event.target.classList){
  if(event.target.classList.contains("DDtarget")){
    event.target.classList.add("DDtarget_hover");
  }
  }
});

// By default, data/elements cannot be dropped in other elements. To allow a drop, we must prevent the default handling of the element
document.addEventListener("dragover", function(event) {
  event.preventDefault();
});

document.addEventListener("dragleave", function(event) {
event.preventDefault();
event.stopPropagation();
  console.log("dragleave");
  if(event.target.classList){
  if(event.target.classList.contains("DDtarget_hover")){
    event.target.classList.remove("DDtarget_hover");
  }
  }
});

document.addEventListener("drop", function(event){
  event.preventDefault();
  event.stopPropagation();
  console.log("drop");
  var SourdeID = event.dataTransfer.getData("Text");
  var TargetID = event.target.getAttribute("id");

  if((SourdeID == "DD1") && (TargetID == "target_4"))
  {
    match(SourdeID, TargetID);
  }
  if((SourdeID == "DD2") && (TargetID == "target_5"))
  {
    match(SourdeID, TargetID);
  }
  if((SourdeID == "DD3") && (TargetID == "target_1"))
  {
    match(SourdeID, TargetID);
  }
  if((SourdeID == "DD4") && (TargetID == "target_3"))
  {
    match(SourdeID, TargetID);
  }
  if((SourdeID == "DD5") && (TargetID == "target_2"))
  {
    match(SourdeID, TargetID);
  }

  if(event.target.classList.contains("DDtarget_hover")){
    event.target.classList.remove("DDtarget_hover");
  }
});

function match(source, target)
{
  var s = document.getElementById(source);
  var t = document.getElementById(target)
  t.appendChild(s);
  if(t.classList.contains("DDtarget_hover")){
    t.classList.remove("DDtarget_hover");
  }
  DDCounter ++;
  if(DDCounter == DDTotalTargets)
  {
    $("#Correct").show();
  }
}

    var DDCounter = 0;
    var DDTotalTargets = 5;
    var DD_X = 80;
    var DDT_X = 180;
    var Txt_X = 280;
    var Txt_W = 920;
    var Y = 120;
    var Offset = 100;
    var Padding = 30;
    var Tw = 80;

function setDDPositions()
{
  for(var i = 0; i <  DDTotalTargets; i++){
    var DD = document.getElementById("DD" + (i + 1));
    var DDT = document.getElementById("target_" + (i + 1));
    var Txt = document.getElementById("text_" + (i + 1));

    DD.style.top = (Y + (Offset * i)) + "px";
    DD.style.left = DD_X + "px";

    DDT.style.top = (Y + (Offset * i)) + "px";
    DDT.style.left = DDT_X + "px";

    Txt.style.position = "absolute";
    Txt.style.top = (Y + (Offset * i)) + "px";
    Txt.style.left = Txt_X + "px";
    Txt.style.width = Txt_W + "px";
    Txt.style.background = "none";
  }
  var BG = document.getElementById("background");
  BG.style.height = ((Offset * DDTotalTargets) + (Padding * 2) - (Offset - Tw)) + "px";
  BG.style.width = (Txt_W + Tw) + "px";
  BG.style.top = (Y - Padding) + "px";
  BG.style.left =  (DDT_X + (Tw / 2)) + "px";
}

setDDPositions();

