function playAudioFx(isCorrect)
{
  var audio = document.getElementById("audio_01");
  audio.pause();
  if(isCorrect)
  {
    audio.setAttribute("src", "course/audios/correct.mp3");
  }
  else{
    audio.setAttribute("src", "course/audios/incorrect.mp3");
  }
  audio.play();
}

function checkanswer(event){
  event.preventDefault;
  var id = event.target.getAttribute("id");
  console.log(id);
  if(id == "target_4")
  {
    if(attemp == 1){
      console.log("congrats");
      document.getElementById("check_4").style.display = "block";
      attemp ++;
      playAudioFx(true);
    }
  }

  if(id == "target_5")
  {
    if(attemp == 2){
      console.log("congrats");
      document.getElementById("check_5").style.display = "block";
      attemp ++;
      playAudioFx(true);
    }
    else{
      document.getElementById("wrong_5").style.display = "block";
      setTimeout(function(){ document.getElementById("wrong_5").style.display = "none"; }, 500);
      playAudioFx(false);
    }
  }

  if(id == "target_1")
  {
    if(attemp == 3){
      console.log("congrats");
      document.getElementById("check_1").style.display = "block";
      attemp ++;
      playAudioFx(true);
    }
    else{
      document.getElementById("wrong_1").style.display = "block";
      setTimeout(function(){ document.getElementById("wrong_1").style.display = "none"; }, 500);
      playAudioFx(false);
    }
  }

  if(id == "target_3")
  {
    if(attemp == 4){
      console.log("congrats");
      document.getElementById("check_3").style.display = "block";
      attemp ++;
      playAudioFx(true);
    }
    else{
      document.getElementById("wrong_3").style.display = "block";
      setTimeout(function(){ document.getElementById("wrong_3").style.display = "none"; }, 500);
      playAudioFx(false);
    }
  }

  if(id == "target_2")
  {
    if(attemp == 5){
      console.log("congrats");
      document.getElementById("check_2").style.display = "block";
      attemp ++;
      playAudioFx(true);
    }
    else{
      document.getElementById("wrong_2").style.display = "block";
      setTimeout(function(){ document.getElementById("wrong_2").style.display = "none"; }, 500);
      playAudioFx(false);
    }
  }

    if(attemp == 6)
    {
      setTimeout(function(){ document.getElementById("Correct").style.display = "block"; }, 1000);
    }
}

