$( document ).ready(function() {
var counterQuestion = 0;
var configMail = [];
configMail.push(["¿Es esta una buena práctica?",
  "si",
  "no",
  false,
  true,
  "No: Recuerda que, como mejor práctica, la comunicación debe ser constante. En esta frase, se evidencia que Luis no contacta a Claudia muy seguido."]);

configMail.push(["¿Es esta una buena práctica?",
  "si",
  "no",
  true,
  false,
  "Sí: Es una mejor práctica identificar y dar seguimiento a las dificultades que se presenten al colaborador en su lugar de trabajo."]);

configMail.push(["¿Es esta una buena práctica?",
  "si",
  "no",
  false,
  true,
  "No: La retroalimentación debe ser uno a uno, oportuna y específica. En este caso, no se abordan con claridad las áreas de oportunidad, ni se proporcionan estrategias para mejorar."]);

configMail.push(["¿Es esta una buena práctica de comunicación?",
  "si",
  "no",
  false,
  true,
  "No: Es correcto plantear los objetivos y tiempos de entrega, sin embargo también es fundamental reconocer el desempeño y motivar al colaborador."]);

function setMailInfo(){
console.log(counterQuestion + " vs " + configMail.length);
if(counterQuestion == configMail.length)
{
  console.log("this is the end");
  document.getElementById("end").style.display = "block";
  return;
}
  document.getElementById("p1").classList.remove("mailFocus");
  document.getElementById("p2").classList.remove("mailFocus");
  document.getElementById("p3").classList.remove("mailFocus");
  document.getElementById("p4").classList.remove("mailFocus");
  document.getElementById("background").style.backgroundColor = "rgba(255,255,255,0.8)";
  document.getElementById("question").innerHTML = configMail[counterQuestion][0];
  document.getElementById("body").style.display = "block";
  document.getElementById("question_contaiter").style.display = "block";
  document.getElementById("feedback").style.display = "none";
  document.getElementById("p" + (counterQuestion + 1)).classList.add("mailFocus");
}

setMailInfo(counterQuestion);

document.getElementById("continuar").addEventListener("click", setMailInfo);
document.getElementById("button_Yes").addEventListener("click", function(){showFeedBack(true);});
document.getElementById("button_No").addEventListener("click", function(){showFeedBack(false);});

function showFeedBack(isLeft){
  var isCorrect = false;
  var hr = document.getElementById("hr");
  var correct = document.getElementById("correct");
  hr.classList.remove("color-green");
  hr.classList.remove("color-red");
  if(isLeft){
    isCorrect = configMail[counterQuestion][3];
  }
  else{
      isCorrect = configMail[counterQuestion][4];
  }
  console.log("isCorrect: " + isCorrect);
  if(isCorrect)
  {
    hr.classList.add("background-green");
    correct.classList.remove("color-red");
    correct.classList.add("color-green");
    correct.innerHTML = "Correcta"
  }
  else{
    hr.classList.add("background-red");
    correct.classList.remove("color-green");
    correct.classList.add("color-red");
    correct.innerHTML = "Incorrecta"
  }

  document.getElementById("background").style.backgroundColor = "rgba(68,57,123,0.8)";
  document.getElementById("body").style.display = "none";
  document.getElementById("question_contaiter").style.display = "none";
  document.getElementById("feedback").style.display = "block";
  document.getElementById("response").innerHTML = configMail[counterQuestion][5];
  counterQuestion++;
}
});

