var attemp = 1;
var target1 = document.getElementById("target_3");
var target2 = document.getElementById("target_1");
var target3 = document.getElementById("target_4");
var target4 = document.getElementById("target_2");

target1.addEventListener("click", checkanswer);
target2.addEventListener("click", checkanswer);
target3.addEventListener("click", checkanswer);
target4.addEventListener("click", checkanswer);

function playAudioFx(isCorrect)
{
  console.log("isCorrect:" + isCorrect);
  if(isCorrect)
  {
    playFxAudio("course/audios/correct.mp3");
  }
  else{
    playFxAudio("course/audios/incorrect.mp3");
  }
}

function checkanswer(event){
  event.preventDefault;
  var id = event.target.getAttribute("id");
  console.log(id);
  if(id == "target_1")
  {
    if(attemp == 1){
      console.log("congrats");
      target2.removeEventListener("click", checkanswer);
      target2.style.backgroundColor = "#6ee2be";
      target2.getElementsByTagName("div")[0].classList.remove("Activity2CircleTarget");
      target2.getElementsByTagName("div")[0].getElementsByTagName("img")[0].setAttribute("src", "course/images/match_1.png");
      document.getElementById("option_1").getElementsByTagName("div")[0].getElementsByTagName("img")[0].setAttribute("src", "course/images/match_1.png");
      document.getElementById("option_2").classList.remove("Activity2Disable");

      attemp ++;
      playAudioFx(true);
    }
  }

  if(id == "target_2")
  {
    if(attemp == 2){
    console.log("congrats");
    target4.removeEventListener("click", checkanswer);
    target4.style.backgroundColor = " #3481f5";
    target4.getElementsByTagName("div")[0].classList.remove("Activity2CircleTarget");
    target4.getElementsByTagName("div")[0].getElementsByTagName("img")[0].setAttribute("src", "course/images/match_2.png");
    document.getElementById("option_2").getElementsByTagName("div")[0].getElementsByTagName("img")[0].setAttribute("src", "course/images/match_2.png");
    document.getElementById("option_3").classList.remove("Activity2Disable");

    attemp ++;
    playAudioFx(true);
    }
    else{
      playAudioFx(false);
      document.getElementById("wrong_2").style.display = "block";
      setTimeout(function(){ document.getElementById("wrong_2").style.display = "none"; }, 500);
    }
  }

  if(id == "target_3")
  {
    if(attemp == 3){
    console.log("congrats");
    target1.removeEventListener("click", checkanswer);
    target1.style.backgroundColor = "#f97168";
    target1.getElementsByTagName("div")[0].classList.remove("Activity2CircleTarget");
    target1.getElementsByTagName("div")[0].getElementsByTagName("img")[0].setAttribute("src", "course/images/match_3.png");
    document.getElementById("option_3").getElementsByTagName("div")[0].getElementsByTagName("img")[0].setAttribute("src", "course/images/match_3.png");
    document.getElementById("option_4").classList.remove("Activity2Disable");

    attemp ++;
    playAudioFx(true);
    }
    else{
      playAudioFx(false);
      document.getElementById("wrong_3").style.display = "block";
      setTimeout(function(){ document.getElementById("wrong_3").style.display = "none"; }, 500);
    }
  }

  if(id == "target_4")
  {
    if(attemp == 4){
    console.log("congrats");
    target3.removeEventListener("click", checkanswer);
    target3.style.backgroundColor = "#50baf5";
    target3.getElementsByTagName("div")[0].classList.remove("Activity2CircleTarget");
    target3.getElementsByTagName("div")[0].getElementsByTagName("img")[0].setAttribute("src", "course/images/match_4.png");
    document.getElementById("option_4").getElementsByTagName("div")[0].getElementsByTagName("img")[0].setAttribute("src", "course/images/match_4.png");

    attemp ++;
    playAudioFx(true);
    }
    else{
      playAudioFx(false);
      document.getElementById("wrong_4").style.display = "block";
      setTimeout(function(){ document.getElementById("wrong_4").style.display = "none"; }, 500);
    }
  }

    if(attemp == 5)
    {
      setTimeout(function(){ document.getElementById("Correct").style.display = "block"; }, 1000);
    }
}
